import logo from './logo.svg';
import { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [listofUsers, setlistofUsers] = useState([{id:1, name:"happy", age:20, username: "happy123"}]);
  const [name, setName] = useState("")
  const [age, setAge] = useState(0)
  const [username,setUsername] =useState("")
  useEffect (()=>{
    // axios.get("http://localhost:3001/getUsers").then((response) => {
    //   setlistofUsers(response.data)
    // })
  },[listofUsers])

  const createUser = () => {
    axios.post("http://localhost:3001/createUser",{ name: name, age : age , username : username}).then((response)=>{
      alert("User Created")
    })
  }

  const uploadFile = () => {
    alert('hi')


  }
  const checkfileUpload = () =>{
    var file;
    document.getElementById('inputfile')
      .addEventListener('change', function () {

        var fr = new FileReader();
        fr.onload = function () {
          document.getElementById('output')
            .textContent = fr.result;
          file = fr.result;
          document.getElementById('size').textContent="Size: "+document.getElementById('inputfile').files[0].size/1024+" KB";
          try {

            var xml = file;
            var xmlDoc = $.parseXML(xml)
            var  $xml = $(xmlDoc)
            var  $title = $xml.find("note");
              alert('valid file')
              axios.post("http://localhost:3001/createUser",{ name: name, age : age , username : username}).then((response)=>{
              alert("User Created")
            })
        } catch (error) {
          alert(error);
        }
        }

        fr.readAsText(this.files[0]);

      })
  }
  return (
    <div className="App">
      <div className="usersDisplay">
        {listofUsers.map((user)=>{
          return (
            <div>
              <h1>Name: {user.name}</h1>
              <h1>age: {user.age}</h1>
              <h1>username: {user.username}</h1>
            </div>
          )
        })
      }</div>

      <div>
        <input type="text" onChange={(event)=>setName(event.target.value)}/>
        <input type="number" onChange={(event)=>setAge(event.target.value)}/>
        <input type="text" onChange={(event)=>setUsername(event.target.value)}/>

        <button onClick={createUser}>Create User</button>
      </div>
      {/* <input type="file" name="inputfile" id="inputfile" onClick={checkfileUpload}></input>
      <br></br>
      <p id="size"></p>
      <pre id="output"></pre> */}

    </div>
  );
}

export default App;
